    }
  }, {
    path: '/*',
    redirect: '/404',
    meta: {
      authRequired: false
    }
  }]
});
router.beforeEach(function (to, from, next) {
  var isAuthorised = store.state.user.isAuthorised;

  if (to.matched.some(function (record) {
    return record.meta.authRequired;
  })) {
    if (!isAuthorised) {
      next({
        name: 'Login',
        query: {
          redirect: to.fullPath
        }
      });
    } else {
      next();
    }
  } else {
    next();
  }
});
export default router;
