import KeyAnimations from '../components/auth/KeyAnimations'

import UIkit from 'uikit/dist/js/uikit'

let notify = (message, status = 'primary', timeout = '2000', pos = 'top-center') => {
  UIkit.notification({
    message: message,
    status:  status,
    pos:     pos,
    timeout: timeout
  })
}

export default {

  name: 'Auth',

  components: {KeyAnimations},

  metaInfo: {
    title:         'Авторизация',
    titleTemplate: '%s - CPK',
    htmlAttrs:     {
      lang: 'ru'
    }
  },

  data () {

    return {

      direction: false,
      motion:    false,

      auth: {
        login:    'admin',
        password: 'admin'
      }

    }

  },

  computed: {

    validateUser () {
      return this.auth.password === 'admin' && this.auth.login === 'admin'
    }
  },

  methods: {

    // авторизация
    authentication () {
      if (this.validateUser) {
        this.$store.commit('setAuthorised', true)
        this.$router.push({name: 'Main'})
      }

    },

    // анимация при вводе логина или пароля
    keyMove () {

      clearTimeout()
      this.direction = Math.random() >= 0.5
      this.motion    = true
      setTimeout(() => this.motion = false, this.randomNumber(150, 800))

    },

    // случайное число для генерации длительности анимации
    randomNumber (min, max) {
      min = min || 100
      max = max || 1000
      return Math.floor(Math.random() * (max + 1 - min)) + min
    }
  }

}
